function elegantwp_font_options($wp_customize) {
    global $elegantwp_font_families;
    global $elegantwp_font_sizes;
    global $elegantwp_font_weights;
    global $elegantwp_font_styles;
    global $elegantwp_font_subsets;

    // Customizer Section: Font Options
    $wp_customize->add_section( 'sc_elegantwp_typography', array( 'title' => esc_html__( 'Font Options', 'elegantwp-pro' ), 'panel' => 'elegantwp_main_options_panel', 'priority' => 220, ));

    $wp_customize->add_setting( 'elegantwp_options[font_subsets]', array( 'default' => array( 'latin' ), 'type' => 'option', 'capability' => 'edit_theme_options', 'sanitize_callback' => 'elegantwp_sanitize_font_subsets' ) );

    $wp_customize->add_control( new ElegantWP_Customize_Multiple_Select( $wp_customize, 'elegantwp_font_subsets_control', array( 'label' => esc_html__( 'Font Subsets', 'elegantwp-pro' ), 'description' => esc_html__('Select which font subsets should be supported.', 'elegantwp-pro'), 'section' => 'sc_elegantwp_typography', 'settings' => 'elegantwp_options[font_subsets]', 'type' => 'tdna-multiple-select', 'choices' => $elegantwp_font_subsets ) ) );

    $wp_customize->add_setting( 'elegantwp_options[body_fonts]', array( 'default' => '//fonts.googleapis.com/css?family=Domine:400,700', 'type' => 'option', 'capability' => 'edit_theme_options', 'sanitize_callback' => 'elegantwp_sanitize_fonts' ) );

    $wp_customize->add_control( 'elegantwp_body_fonts_control', array( 'label' => esc_html__( 'Body Font-Family', 'elegantwp-pro' ), 'description' => __('Pick a font for body text. Default is <b>Domine.</b>', 'elegantwp-pro'), 'section' => 'sc_elegantwp_typography', 'settings' => 'elegantwp_options[body_fonts]', 'type' => 'select', 'choices' => $elegantwp_font_families ) );

    $wp_customize->add_setting( 'elegantwp_options[body_font_size]', array( 'default' => '13px', 'type' => 'option', 'capability' => 'edit_theme_options', 'sanitize_callback' => 'elegantwp_sanitize_font_sizes' ) );

    $wp_customize->add_control( 'elegantwp_body_font_size_control', array( 'label' => esc_html__( 'Body Font-Size', 'elegantwp-pro' ), 'description' => __('Choose body font size. Default is <b>13px.</b>', 'elegantwp-pro'), 'section' => 'sc_elegantwp_typography', 'settings' => 'elegantwp_options[body_font_size]', 'type' => 'select', 'choices' => $elegantwp_font_sizes ) );

    $wp_customize->add_setting( 'elegantwp_options[body_font_weight]', array( 'default' => '400', 'type' => 'option', 'capability' => 'edit_theme_options', 'sanitize_callback' => 'elegantwp_sanitize_font_weights' ) );

    $wp_customize->add_control( 'elegantwp_body_font_weight_control', array( 'label' => esc_html__( 'Body Font-Weight', 'elegantwp-pro' ), 'description' => __('Choose body font weight. Default is <b>400.</b>', 'elegantwp-pro'), 'section' => 'sc_elegantwp_typography', 'settings' => 'elegantwp_options[body_font_weight]', 'type' => 'select', 'choices' => $elegantwp_font_weights ) );

    $wp_customize->add_setting( 'elegantwp_options[body_font_style]', array( 'default' => 'normal', 'type' => 'option', 'capability' => 'edit_theme_options', 'sanitize_callback' => 'elegantwp_sanitize_font_styles' ) );

    $wp_customize->add_control( 'elegantwp_body_font_style_control', array( 'label' => esc_html__( 'Body Font-Style', 'elegantwp-pro' ), 'description' => __('Choose body font style. Default is <b>normal.</b>', 'elegantwp-pro'), 'section' => 'sc_elegantwp_typography', 'settings' => 'elegantwp_options[body_font_style]', 'type' => 'select', 'choices' => $elegantwp_font_styles ) );

